const translationImg = document.querySelectorAll('.translate-img');
let translation;

let translate = function (lang) {
    translation = document.querySelectorAll('.translate');
    translation.forEach((element) => {
        element.innerHTML = dict[lang][element.id];
    });
    translationImg.forEach((element) => {
        element.src = dict[lang][element.id];
    });
    localStorage.lang = lang;
};
if (localStorage.lang == undefined) {
    localStorage.lang = 'Hr';
}
translate(localStorage.lang);
